import React, { Component } from "react";
import { Link, Redirect } from "react-router-dom";
import { Container, Row, Col } from "reactstrap";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button
} from "reactstrap";

import { Input } from "reactstrap";
import axios from "axios";

export class lista_profesora extends Component {
  constructor(props) {
    super(props);

    this.state = {
      search: "",
      profesors: []
    };
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/", { withCredentials: true })
      .then(response => {
        this.setState({ profesors: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }
  renderCountry = c => {
    const { search } = this.state;

    if (
      search !== "" &&
      c.ime.toLowerCase().indexOf(search.toLowerCase()) === -1
    ) {
      return null;
    }

    return (
      <Col sm={4}>
        <Card key={c._id}>
          <CardImg
            top
            src="data:image/jpeg;base64,"
            alt="Card image cap"
          />
          <CardBody>
            <CardTitle style={{ fontSize: 20 }}>{c.ime}</CardTitle>
            <CardSubtitle style={{ fontSize: 20 }}>{c.prezime}</CardSubtitle>
            <CardText style={{ fontSize: 20 }}>{c.akademskiStupanj}</CardText>
            <Link to={`/profesors/${c._id}`} className="btn btn-info">
              Vi≈°e
            </Link>
          </CardBody>
        </Card>
      </Col>
    );
  };
  onchange = e => {
    this.setState({ search: e.target.value });
  };

  onSubmit(e) {
    e.preventDefault();

    axios
      .get("http://localhost:5000/logout", { withCredentials: true })
      .then(response => {
        console.log(response.data);
        if (response.status === 200) {
          this.setState({
            logiran: false,
            username: null
          });
        }
      });
  }
  renderRedirect = () => {
    window.location.reload(true);
  };
  render() {
    const { search } = this.state;
    const filterFunkcija = this.state.profesors.filter(c => {
      return c.ime.toLowerCase().indexOf(search.toLowerCase()) !== -1;
    });

    return (
      <Container>
        {/*   <form onSubmit={this.onSubmit}>
          <button type="submit">Logout</button>
        </form */}

        <div className="col">
          <Input
            placeholder="Pretra≈æi profesora"
            label="Search Country"
            icon="search"
            onChange={this.onchange}
          />
        </div>
        <Row>
          {filterFunkcija.map(c => {
            return this.renderCountry(c);
          })}
        </Row>
      </Container>
    );
  }
}

export default lista_profesora;
